ercontent.com/u/70805946?v=4","public":true,"private":false,"isOrgOwned":false},"codeLineWrapEnabled":false,"symbolsExpanded":false,"treeExpanded":true,"refInfo":{"name":"master","listCacheKey":"v0:1718312371.9936588","canEdit":false,"refType":"branch","currentOid":"2162d546e8a68ba46bab82bc524ff6a01938b97a"},"path":"tools/dockerfiles/scripts/run_deseq_lrt.R","currentUser":null,"blob":{"rawLines":["#!/usr/bin/env Rscript","options(warn=-1)","options(\"width\"=400)","","suppressMessages(library(argparse))","suppressMessages(library(BiocParallel))","suppressMessages(library(pheatmap))","suppressMessages(library(DESeq2))","","################################################################################################","# v0.0.1","#","# Note: at least two biological replicates are required for every compared category.","#","# All input CSV/TSV files should have the following header (case-sensitive)","# \u003cRefseqId,GeneId,Chrom,TxStart,TxEnd,Strand,TotalReads,Rpkm\u003e         - CSV","# \u003cRefseqId\\tGeneId\\tChrom\\tTxStart\\tTxEnd\\tStrand\\tTotalReads\\tRpkm\u003e  - TSV","#","# Format of the input files is identified based on file's extension","# *.csv - CSV","# *.tsv - TSV","# Otherwise used CSV by default","#","# The output file's rows order corresponds to the rows order of the first CSV/TSV file.","# Output file is always saved in TSV format","#","# Output file includes only intersected rows from all input files. Intersected by","# RefseqId, GeneId, Chrom, TxStart, TxEnd, Strand","#","# Additionally we calculate -LOG10(pval) and -LOG10(padj)","#","# Example of CSV metadata file set with --meta","#","# ,time,condition","# DH1,day5,WT","# DH2,day5,KO","# DH3,day7,WT","# DH4,day7,KO","# DH5,day7,KO","#","# where time, condition, day5, day7, WT, KO should be a single words (without spaces)","# and DH1, DH2, DH3, DH4, DH5 correspond to the --names (spaces are allowed)","#","# --contrast should be set based on your metadata file in a form of Factor Numerator Denominator","# where Factor      - columns name from metadata file","#       Numerator   - category from metadata file to be used as numerator in fold change calculation","#       Denominator - category from metadata file to be used as denominator in fold change calculation","# for example condition WT KO","# if --contrast is set as a single string \"condition WT KO\" then is will be splitted by space","#","################################################################################################","","","READ_COL \u003c- \"TotalReads\"","RPKM_COL \u003c- \"Rpkm\"","INTERSECT_BY \u003c- c(\"RefseqId\", \"GeneId\", \"Chrom\", \"TxStart\", \"TxEnd\", \"Strand\")","","","get_file_type \u003c- function (filename) {","  ext = tools::file_ext(filename)","  separator = \",\"","  if (ext == \"tsv\"){","    separator = \"\\t\"","  }","  return (separator)","}","","","load_expression_data \u003c- function(filenames, prefixes, read_colname, rpkm_colname, intersect_by) {","  collected_isoforms=NULL","  for (i in 1:length(filenames)) {","    isoforms \u003c- read.table(filenames[i], sep=get_file_type(filenames[i]), header=TRUE, stringsAsFactors=FALSE)","    print(paste(\"Load \", nrow(isoforms), \" rows from \", filenames[i], sep=\"\"))","    colnames(isoforms)[colnames(isoforms) == read_colname] \u003c- paste(prefixes[i], read_colname, sep=\" \")","    colnames(isoforms)[colnames(isoforms) == rpkm_colname] \u003c- paste(prefixes[i], rpkm_colname, sep=\" \")","    if (is.null(collected_isoforms)){","      collected_isoforms \u003c- isoforms","    } else {","      collected_isoforms \u003c- merge(collected_isoforms, isoforms, by=intersect_by, sort = FALSE)","    }","  }","  print(paste(\"Number of rows common for all loaded files \", nrow(collected_isoforms), sep=\"\"))","  return (collected_isoforms)","}","","","assert_args \u003c- function(args){","  if ( length(args$input) != length(args$name) ){","    print(\"Exiting: --input and --name have different number of values\")","    quit(save = \"no\", status = 1, runLast = FALSE)","  }","  if ( length(args$contrast) != 3 ){","    args$contrast = unlist(strsplit(args$contrast,\"\\\\s+\",fixed=FALSE))  # split by any number of spaces","    print(\"Split --contrast by spaces\")","    print(args$contrast)","    if ( length(args$contrast) != 3 ){","      print(\"Exiting: --contrast should have exaclty three values\")","      quit(save = \"no\", status = 1, runLast = FALSE)","    }","  }","  tryCatch(","    expr = {","      # Try to load design formula","      design_formula = as.formula(args$design)","    },","    error = function(e){ ","      print(paste(\"Exiting: failed to load --design \", args$design, \" as formula\",  sep=\"\"))","      quit(save = \"no\", status = 1, runLast = FALSE)","    }","  )","  tryCatch(","    expr = {","      # Try to load reduced formula","      reduced_formula = as.formula(args$reduced)","    },","    error = function(e){ ","      print(paste(\"Exiting: failed to load --reduced \", args$reduced, \" as formula\",  sep=\"\"))","      quit(save = \"no\", status = 1, runLast = FALSE)","    }","  )","  return (args)","}","","","get_args \u003c- function(){","  parser \u003c- ArgumentParser(description=\"Run DeSeq2 for multi-factor analysis using LRT (likelihood ratio or chi-squared test)\")","  parser$add_argument(\"-i\", \"--input\",    help=\"Grouped by gene / TSS/ isoform expression files, formatted as CSV/TSV\",                                                       type=\"character\", required=\"True\", nargs=\"+\")","  parser$add_argument(\"-n\", \"--name\",     help=\"Unique names for input files, no special characters, spaces are allowed. Number and order corresponds to --input\",            type=\"character\", required=\"True\", nargs=\"+\")","  parser$add_argument(\"-m\", \"--meta\",     help=\"Metadata file to describe relation between samples, where first column corresponds to --name, formatted as CSV/TSV\",          type=\"character\", required=\"True\")","  parser$add_argument(\"-d\", \"--design\",   help=\"Design formula. Should start with ~. See DeSeq2 manual for details\",                                                          type=\"character\", required=\"True\")","  parser$add_argument(\"-r\", \"--reduced\",  help=\"Reduced formula to compare against with the term(s) of interest removed. Should start with ~. See DeSeq2 manual for details\", type=\"character\", required=\"True\")","  parser$add_argument(\"-c\", \"--contrast\", help=\"Contrast to be be applied for output, formatted as Factor Numerator Denominator or Factor Numerator Denominator\",           type=\"character\", required=\"True\", nargs=\"+\")","  parser$add_argument(\"-o\", \"--output\",   help=\"Output prefix for generated files\", type=\"character\", default=\"./deseq\")","  parser$add_argument(\"-p\", \"--threads\",  help=\"Threads number\",                    type=\"integer\",   default=1)","  args \u003c- assert_args(parser$parse_args(commandArgs(trailingOnly = TRUE)))","  return (args)","}","","","# Parse arguments","args \u003c- get_args()","","# Set threads","register(MulticoreParam(args$threads))","","# Load metadata","metadata_df \u003c- read.table(args$meta, sep=get_file_type(args$meta), header=TRUE, stringsAsFactors=FALSE, row.names=1)","print(paste(\"Load metadata from\", args$meta, sep=\" \"))","print(metadata_df)","","# Load design formula","design_formula = as.formula(args$design)","print(\"Load design formula\")","print(design_formula)","","# Load reduced formula","reduced_formula = as.formula(args$reduced)","print(\"Load reduced formula\")","print(reduced_formula)","","# Load expression data","expression_data_df \u003c- load_expression_data(args$input, args$name, READ_COL, RPKM_COL, INTERSECT_BY)","print(\"Expression data\")","print(head(expression_data_df))","","# Select all columns with read counts data, reorder them based on the row names from metadata_df","read_counts_columns = grep(paste(READ_COL, sep=\"\"), colnames(expression_data_df), value = TRUE, ignore.case = TRUE)","read_counts_data_df = expression_data_df[read_counts_columns]","colnames(read_counts_data_df) \u003c- lapply(colnames(read_counts_data_df), function(s){paste(head(unlist(strsplit(s,\" \",fixed=TRUE)),-1), collapse=\" \")})","print(\"Read counts data\")","print(head(read_counts_data_df))","tryCatch(","  expr = {","    # Try reorder columns in read_counts_data_df based on metadata_df rownames","    print(\"Reorder read count columns based on the row names from the provided metadata file\")","    read_counts_data_df = read_counts_data_df[,rownames(metadata_df)]","    print(head(read_counts_data_df))","  },","  error = function(e){ ","    print(\"Exiting: failed to reorder read count columns based on the row names from the provided metadata file\")","    print(paste(\"Count data columns -\", paste(colnames(read_counts_data_df), collapse=\" \"), sep=\" \"))","    print(paste(\"Metadata file rows -\", paste(rownames(metadata_df), collapse=\" \"), sep=\" \"))","    quit(save = \"no\", status = 1, runLast = FALSE)","  }",")","","print(\"Run DESeq2 using LRT\")","dse \u003c- DESeqDataSetFromMatrix(countData=read_counts_data_df, colData=metadata_df, design=design_formula)","dsq \u003c- DESeq(dse, test=\"LRT\", reduced=reduced_formula, quiet=TRUE, parallel=TRUE)","res \u003c- results(dsq, contrast=args$contrast)","print(\"Results description\")","print(mcols(res))","","# Save MA-plot","filename \u003c- paste(args$output, \"_ma_plot.png\", sep=\"\")","print(paste(\"Save MA-plot to\", filename, sep=\" \"))","png(filename=filename, width=800, height=800)","plotMA(res, main=\"MA-plot\")","","# Filter DESeq2 output","res_filtered \u003c- as.data.frame(res[,c(2,5,6)])","res_filtered$log2FoldChange[is.na(res_filtered$log2FoldChange)] = 0;","res_filtered[is.na(res_filtered)] = 1;","","# Export results to TSV file","expression_data_df = data.frame(cbind(expression_data_df[,], res_filtered), check.names=F, check.rows=F)","expression_data_df[,\"-LOG10(pval)\"] \u003c- -log(as.numeric(expression_data_df$pval), 10)","expression_data_df[,\"-LOG10(padj)\"] \u003c- -log(as.numeric(expression_data_df$padj), 10)","","filename \u003c- paste(args$output, \"_table.tsv\", sep=\"\")","write.table(expression_data_df,","            file=filename,","            sep=\"\\t\",","            row.names=FALSE,","            col.names=TRUE,","            quote=FALSE)","print(paste(\"Export results to\", filename, sep=\" \"))","graphics.off()"],"stylingDirectives":[[{"s":0,"e":22,"c":"pl-c"},{"s":0,"e":1,"c":"pl-c"}],[{"s":8,"e":12,"c":"pl-v"},{"s":12,"e":13,"c":"pl-k"},{"s":13,"e":14,"c":"pl-k"},{"s":14,"e":15,"c":"pl-c1"}],[{"s":8,"e":15,"c":"pl-s"},{"s":8,"e":9,"c":"pl-pds"},{"s":14,"e":15,"c":"pl-pds"},{"s":15,"e":16,"c":"pl-k"},{"s":16,"e":19,"c":"pl-c1"}],[],[{"s":25,"e":33,"c":"pl-smi"}],[{"s":25,"e":37,"c":"pl-smi"}],[{"s":25,"e":33,"c":"pl-smi"}],[{"s":25,"e":31,"c":"pl-smi"}],[],[{"s":0,"e":96,"c":"pl-c"},{"s":0,"e":1,"c":"pl-c"}],[{"s":0,"e":8,"c":"pl-c"},{"s":0,"e":1,"c":"pl-c"}],[{"s":0,"e":1,"c":"pl-c"},{"s":0,"e":1,"c":"pl-c"}],[{"s":0,"e":84,"c":"pl-c"},{"s":0,"e":1,"c":"pl-c"}],[{"s":0,"e":1,"c":"pl-c"},{"s":0,"e":1,"c":"pl-c"}],[{"s":0,"e":75,"c":"pl-c"},{"s":0,"e":1,"c":"pl-c"}],[{"s":0,"e":76,"c":"pl-c"},{"s":0,"e":1,"c":"pl-c"}],[{"s":0,"e":76,"c":"pl-c"},{"s":0,"e":1,"c":"pl-c"}],[{"s":0,"e":1,"c":"pl-c"},{"s":0,"e":1,"c":"pl-c"}],[{"s":0,"e":67,"c":"pl-c"},{"s":0,"e":1,"c":"pl-c"}],[{"s":0,"e":13,"c":"pl-c"},{"s":0,"e":1,"c":"pl-c"}],[{"s":0,"e":13,"c":"pl-c"},{"s":0,"e":1,"c":"pl-c"}],[{"s":0,"e":31,"c":"pl-c"},{"s":0,"e":1,"c":"pl-c"}],[{"s":0,"e":1,"c":"pl-c"},{"s":0,"e":1,"c":"pl-c"}],[{"s":0,"e":87,"c":"pl-c"},{"s":0,"e":1,"c":"pl-c"}],[{"s":0,"e":43,"c":"pl-c"},{"s":0,"e":1,"c":"pl-c"}],[{"s":0,"e":1,"c":"pl-c"},{"s":0,"e":1,"c":"pl-c"}],[{"s":0,"e":81,"c":"pl-c"},{"s":0,"e":1,"c":"pl-c"}],[{"s":0,"e":49,"c":"pl-c"},{"s":0,"e":1,"c":"pl-c"}],[{"s":0,"e":1,"c":"pl-c"},{"s":0,"e":1,"c":"